	@Test
	public void shouldGetASimpleJSONBody() {
		// given
		BufferedReader reader = new BufferedReader(new StringReader("""
                                                                 {"a":"b"}
                                                                 
                                                                 """));
		// when
		String result = HttpResponse.getBody(reader).toString();
		// then
		assertEquals("{\"a\":\"b\"}", result);
	}

	@Test
	public void shouldGetAChunkedStringBody() {
		// given
		BufferedReader reader = new BufferedReader(new StringReader("""
				7
				Mozilla
				11
				Developer Network
				0
				
				                                                             """));
		// when
		String result = HttpResponse.getChunkedBody(reader);
		// then
		assertEquals("MozillaDeveloper Network", result);
	}

